/**
 * Writes a JSON object to a file
 * @param serverUrlHash The hash of the server URL
 * @param filename The name of the file to write
 * @param data The data to write
 */
declare function writeJsonFile(serverUrlHash: string, filename: string, data: any): Promise<void>;

declare global {
    var currentServerUrlHash: string | undefined;
}
/**
 * Generates a hash for the server URL to use in filenames
 * @param serverUrl The server URL to hash
 * @returns The hashed server URL
 */
declare function getServerUrlHash(serverUrl: string): string;

export { getServerUrlHash, writeJsonFile };
